var BtnOpen = document.querySelector(".btn--open-menu");
var BtnClose = document.querySelector(".btn--close-menu");
var Menu = document.querySelector(".main-nav");

BtnOpen.addEventListener("click", function(b) {
    b.preventDefault(), Menu.classList.toggle("main-nav--show")
})

BtnClose.addEventListener("click", function(event) {
	event.preventDefault();
	Menu.classList.remove("main-nav--show");
});

var link = document.querySelector(".btn--search"),
    search_form = document.querySelector(".search-hotel");

link.addEventListener("click", function(a) {
    a.preventDefault(), search_form.classList.toggle("search-hotel--show")
})
